"""
When invoking a variable in Python, the interpreter looks for it in this order:
Locals -> Enclosing function -> Globals -> Builtins, AKA the LEGB rule.
"""

import multiprocessing
import sys
import os

print("all global variables:", globals())
print("all built-in functions:", dir(__builtins__))

print("\n----------------------------------1----------------------------------")
# When importing a package/module, python searches it from directories in sys.path.
# Empty quotes "" means current dir. Use append or insert to temporarily add extra dirs.
print(sys.path)

# the path of current file
# if current file is in sys.path, this shows relative path. if not, show absolute path.
print(__file__)
# the directory of current file
print(os.path.dirname(__file__))

print("\n----------------------------------2----------------------------------")
# list of attributes and methods of os module (or any object like functions , modules, classes etc.)
print(dir(os))

print("\n----------------------------------3----------------------------------")
# dict of all attributes of os module
print(os.__dict__)

print("\n----------------------------------4----------------------------------")
print("The number of current CPU core is:", multiprocessing.cpu_count())

print("\n----------------------------------5----------------------------------")
# check the size of memory a variable takes up
print(f"an integer takes up {sys.getsizeof(int())} bytes. a string takes up {str().__sizeof__()} bytes")
# sys.getsizeof(xx) is equivalent to xx.__sizeof__()
print(f"integer 1 takes up {sys.getsizeof(1)} bytes. string 'a' takes up {'a'.__sizeof__()} bytes")


